### myget_endofday ###
sub myget_endofday
{
    my ($srv, $usr, $pwd) = ("dbi:Oracle:URBIS", "linc", "unix");
    my ($dbh, $sth, $sql, $row, @row);
    $dbh = DBI->connect($srv, $usr, $pwd) || die("could not connect to $srv:[$dbh->errstr]");
    $sql = "SELECT TO_CHAR(TO_DATE(TO_CHAR(LAST_DATE, '000000'), 'DDMMYY'), 'YYYYMMDD') FROM SYSTWODB_DATES WHERE ONOFF_IND = 'ON'";
    $sth = $dbh->prepare($sql) || die("could not prepare $sql:[$dbh->errstr]");
    $sth->execute() || die("could not execute $sql:[$dbh->errstr]");
    while (@row = $sth->fetchrow()) {
        $row = $row[0];
    }
    $sth->finish();
    $dbh->disconnect();
    return($row);
}

### multi_unloader ###
sub multi_unloader
{
    my ($sys, $srv, $usr, $pwd, $dbh, $sth, $txt, $sql, $fth, $row, @row);
    for $sys (sort keys %{env}) {
        printf("------------------starting process:[%s]------------------\n", $sys);
        ($srv, $usr, $pwd) = @{$env{$sys}{'dbs'}{'adp'}};
        $dbh = DBI->connect($srv, $usr, $pwd) || die("could not connect to $srv:[$dbh->errstr]");
        while (($txt, $sql) = each(%{$env{$sys}{'dbs'}{'cfg'}})) {
            printf("multi-unloader: txt-file:[%s]\n", $txt);
            open($fth, ">$txt") || die("could not open $txt:[$!]");
            $sth = $dbh->prepare($sql) || die("could not prepare $sql:[$dbh->errstr]");
            $sth->execute() || die("could not execute $sql:[$dbh->errstr]");
            while (@row = $sth->fetchrow()) {
                foreach (@row) { $_ = '' unless defined; }
                $row = join("|", @row);
                print $fth "$row|\n";
            }
            $sth->finish();
            close($fth);
        }
        $dbh->disconnect();
        printf("------------------finished process:[%s]------------------\n", $sys);
    }
    return(0);
}

### multi_compress ###
sub multi_compress
{
    my ($sys, $tar, $lst);
    for $sys (sort keys %{env}) {
        printf("------------------starting process:[%s]------------------\n", $sys);
        while (($tar, $lst) = each(%{$env{$sys}{'dbs'}{'via'}})) {
            printf("multi-compress: tar-file:[%s]\n", $tar);
            system("tar -cvf $tar $lst");
            system("gzip $tar");
            system("del  $lst");
        }
        printf("------------------finished process:[%s]------------------\n", $sys);
    }
    return(0);
}

### multi_sendlist ###
sub multi_sendlist
{
    my ($sys, $snd, $row, $fth);
    for $sys (sort keys %{env}) {
        printf("------------------starting process:[%s]------------------\n", $sys);
        while (($snd, $row) = each(%{$env{$sys}{'ftp'}{'via'}})) {
            printf("multi-sendlist: snd-file:[%s]\n", $snd);
            open($fth, ">$snd") || die("could not open $snd:[$!]");
            print $fth "$row\n";
            close($fth);
        }
        printf("------------------finished process:[%s]------------------\n", $sys);
    }
    return(0);
}

### multi_transfer ###
sub multi_transfer
{
    my ($sys, $srv, $usr, $pwd, $ftp, $pkg, $rmd);
    for $sys (sort keys %{env}) {
        printf("------------------starting process:[%s]------------------\n", $sys);
        ($srv, $usr, $pwd) = @{$env{$sys}{'ftp'}{'adp'}};
        $ftp = Net::FTP->new($srv) || die("could not connect to $srv:[$ftp->message]");
        $ftp->login($usr, $pwd) || die("could not login:[$ftp->message]");
        $ftp->binary() || die("could not change mode:[$ftp->message]");
        while (($pkg, $rmd) = each(%{$env{$sys}{'ftp'}{'cfg'}})) {
            printf("multi-transfer: pkg-file:[%s]\n", $pkg);
            $ftp->mkdir($rmd, "true") || die("could not execute mkdir:[$ftp->message]");
            $ftp->cwd($rmd) || die("could not execute mkdir:[$ftp->message]");
            $ftp->put($pkg) || die("could not execute put:[$ftp->message]");
        }
        $ftp->quit();
        printf("------------------finished process:[%s]------------------\n", $sys);
    }
    mkdir("$eod");
    system("move *.list   $eod");
    system("move *.tar.gz $eod");
    return(0);
}
